mapper.update:
update look_member set
passwd=#{passwd}
where id=#{id}

mapper.select:
select
count(*) from look_member
where id =#{id}

select
count(*) from look_member
where email =#{email}

select
count(*) from look_member
where id = #{id}
and passwd = #{passwd}

select
grade from look_member
where id=#{id}
